import React from 'react';
import FuseExample from '@fuse/core/FuseExample';
import FuseHighlight from '@fuse/core/FuseHighlight';
import Button from '@material-ui/core/Button';
import Icon from '@material-ui/core/Icon';
import Typography from '@material-ui/core/Typography';
import {makeStyles} from '@material-ui/core/styles';
/* eslint import/no-webpack-loader-syntax: off */
/* eslint no-unused-vars: off */
/* eslint-disable jsx-a11y/accessible-emoji */
const useStyles = makeStyles(theme => ({
    layoutRoot: {
        '& .description': {
            marginBottom: 16
        }
    }
}));

function SelectsDoc(props)
{
    const classes = useStyles();
    return (

        <>
            <div className="flex flex-1 flex-grow-0 items-center justify-end">
                <Button
                    className="normal-case"
                    variant="outlined"
                    component="a"
                    href="https://material-ui.com/components/selects"
                    target="_blank"
                    role="button"
                >
                    <Icon>link</Icon>
                    <span className="mx-4">Reference</span>
                </Button>
            </div>
            <Typography className="text-44 mt-32 mb-8" component="h1">Select</Typography>
            <Typography className="description">Select components are used for collecting user provided information from a list of options.</Typography>

            <Typography className="text-32 mt-32 mb-8" component="h2">Simple Select</Typography>
            <Typography className="mb-16" component="div">Menus are positioned over their emitting elements such that the currently selected menu item appears on top of the emitting
                element.</Typography>
            <Typography className="mb-16" component="div"><FuseExample
                className="my-24"
                iframe={false}
                component={require('app/main/documentation/material-ui-components/components/selects/SimpleSelect.js').default}
                raw={require('!raw-loader!app/main/documentation/material-ui-components/components/selects/SimpleSelect.js')}
            /></Typography>
            <Typography className="text-32 mt-32 mb-8" component="h2">Advanced features</Typography>
            <Typography className="mb-16" component="div">The Select component is meant to be interchangeable with a native <code>{`<select>`}</code> element.</Typography>
            <Typography className="mb-16" component="div">If you are looking for more advanced features, like combobox, multiselect, autocomplete, async or creatable support, head to the <a
