import React from 'react';
import { makeStyles } from '@material-ui/core/styles';
import Stepper from '@material-ui/core/Stepper';
import Step from '@material-ui/core/Step';
import StepLabel from '@material-ui/core/StepLabel';
import Button from '@material-ui/core/Button';
import Typography from '@material-ui/core/Typography';
import FuseAnimate from '@fuse/core/FuseAnimate';
import Icon from '@material-ui/core/Icon';
import clsx from 'clsx';
import BasicTextFields from './GoesEmpresas';

import Fab from '@material-ui/core/Fab';
import AddIcon from '@material-ui/icons/Add';



const useStyles = makeStyles((theme) => ({
    root: {
        width: '100%',
    },
    backButton: {
        marginRight: theme.spacing(1),
    },
    instructions: {
        marginTop: theme.spacing(1),
        marginBottom: theme.spacing(1),
    },
}));


function getSteps() {
    return [
        'Crear GAES',
        '¿ Que Tipo de Empesa quieres ?',
        '¿ Como se llamará la Empresa ?',
        '¿ Cuales son los dato de contacto de la Empresa ?',
        '¿ Es necesario definir quien es Empresa y hacia donde va ?',
        '¿ Ahora vamos a crear el modelo de negocio de la Empresa ?',
        '¿ solo falta el logotipo de la Empresa ?',
    ];
}


function getStepContent(stepIndex) {
    switch (stepIndex) {
        case 0:
            return <BasicTextFields/>
        case 1:
            return <BasicTextFields />
        case 2:
            return <BasicTextFields />
        case 3:
            return <BasicTextFields />
        case 4:
            return <BasicTextFields />
        case 5:
            return <BasicTextFields />
        case 6:
            return <BasicTextFields />
        default:
            return 'Unknown stepIndex';
    }
}

function getStepTexto(stepIndex) {
    switch (stepIndex) {
        case 0:
            return '¡Vamos a Crear Grupos de aprendizaje de  Estudiantes GAES!';
        case 1:
            return '¿ Que Tipo de Empesa quieres ?';
        case 2:
            return '¿ Como se llamará la Empresa ?';
        case 3:
            return '¿ Cuales son los dato de contacto de la Empresa ?';
        case 4:
            return '¿ Es necesario definir quien es Empresa y hacia donde va ?';
        case 5:
            return '¿ Ahora vamos a crear el modelo de negocio de la Empresa ?';
        case 6:
            return '¿ solo falta el logotipo de la Empresa ?';
        default:
            return 'Unknown stepIndex';
    }
}

export default function HorizontalLabelPositionBelowStepper() {
    const classes = useStyles();
    const [activeStep, setActiveStep] = React.useState(0);
    const steps = getSteps();

    const handleNext = () => {
        setActiveStep((prevActiveStep) => prevActiveStep + 1);
    };

    const handleBack = () => {
        setActiveStep((prevActiveStep) => prevActiveStep - 1);
    };

  /*   const handleReset = () => {
        setActiveStep(0);
    }; */

    return (
        <div className={classes.root}>
           <div>
            <Stepper activeStep={activeStep} alternativeLabel>
                {steps.map((label) => (
                    <Step key={label}>
                        <StepLabel>{label}</StepLabel>
                    </Step>
                ))}
                
                </Stepper>
            </div>
            <div
                className={clsx(
                    classes.header,
                    'relative overflow-hidden flex flex-col flex-shrink-0 items-center justify-center text-center p-16 sm:p-24 h-100 sm:h-90 bg-white'
                )}
            >
                <FuseAnimate animation="transition.slideUpIn" duration={400} delay={100}>
                    <Typography color="inherit" className="text-24 sm:text-40 font-light">
                        !BIENVENIDO¡
					</Typography>
                </FuseAnimate>
                <FuseAnimate duration={400} delay={600}>
                    <Typography variant="subtitle1" color="inherit" className="mt-8 sm:mt-16 mx-auto max-w-512">
                        <div>
                            {activeStep === steps.length ? (
                                <div>
                                    <Typography className={classes.instructions}>
                                        Felicidades tu empresa ya esta creada
                            </Typography>
                                    <Button onClick={handleBack}>Regresar</Button>
                                </div>
                            ) : (
                                    <div>
                                        <span className="opacity-75">
                                                <Typography className={classes.instructions}>
                                                {getStepTexto(activeStep)}
                                            </Typography>
						                 </span>
                                     

                                        <Typography className={classes.instructions}>
                                            {getStepContent(activeStep)}
                                        </Typography>
                                        <div>
                                            
                                            <Fab
                                                disabled={activeStep === 0}
                                                onClick={handleBack}
                                                size="small"
                                                color="primary"
                                                aria-label="add"
                                                className={classes.margin}>
                                                    <AddIcon />
                                                </Fab>
                                         {/*    <Button
                                                disabled={activeStep === 0}
                                                onClick={handleBack}
                                                className={classes.backButton}
                                                variant="contained"
                                                color="primary"
                                            >
                                                Atras
                                            </Button> */}
                                            <Button variant="contained" color="primary" onClick={handleNext}>
                                                {activeStep === steps.length - 1 ? 'Fin' : 'Siguiente'}
                                            </Button>
                                        </div>
                                    </div>
                                )}
                        </div>
                    </Typography>
                </FuseAnimate>
                 <Icon className={classes.headerIcon}> school </Icon>
            </div>

        </div>
    );
}
