import _ from '@lodash';
import {blue } from '@material-ui/core/colors';
//import { amber, blue, blueGrey, green } from '@material-ui/core/colors';
import mock from '../mock';
import React from 'react';
import clsx from 'clsx';



const demoSteps = [
	{
		id: '0',
		title: 'Creacion Grupo de aprendizaje',
		content:
			'<h1>Momento 1 - Vamos a crear Grupo de aprendizaje de studiantes del Sena GAES</h1>' +
			'<br>'
	},
	{
		id: '1',
		title: 'Tipo de Empresa',
		content:
			'<h1>Momento 2 - Vamos a crear tu Empresa</h1>' +
			'<br>'



	},
	{
		id: '2',
		title: 'Como se llamara tu Empresa',
		content:
			'<h1>Momento 3 - Vamos a crear tu Empresa</h1>' +
			'<br>'
	},
];

const academyDB = {
	categories: [
		{
			id: 0,
			value: 'web',
			label: 'Web',
			color: blue[500]
		}
		/* {
			id: 1,
			value: 'firebase',
			label: 'Firebase',
			color: amber[500]
		},
		{
			id: 2,
			value: 'cloud',
			label: 'Cloud',
			color: blueGrey[500]
		},
		{
			id: 3,
			value: 'android',
			label: 'Android',
			color: green[500]
		} */
	],
	courses: [
		{
			id: '154588a0864d2881124',
			title: 'Basics of TypeScript',
			slug: 'basics-of-typeScript',
			description: 'Commits that need to be pushed lorem ipsum dolor sit amet, consectetur adipiscing elit.',
			category: 'web',
			length: 0,
			totalSteps: 6,
			activeStep: 0,
			updated: 'Nov 01, 2017',
			steps: demoSteps
		}
	]
};

mock.onGet('/api/academy-app/categories').reply(() => {
	return [200, academyDB.categories];
});

mock.onGet('/api/academy-app/courses').reply(() => {
	return [200, academyDB.courses.map(_course => _.omit(_course, ['steps']))];
});

mock.onGet('/api/academy-app/course').reply(request => {
	const { courseId } = request.params;
	const response = _.find(academyDB.courses, { id: courseId });
	return [200, response];
});

 mock.onPost('/api/academy-app/course/save').reply(request => {
	const data = JSON.parse(request.data);
	let course = null;

	academyDB.courses = academyDB.courses.map(_course => {
		if (_course.id === data.id) {
			course = data;
			return course;
		}
		return _course;
	});

	if (!course) {
		course = data;
		academyDB.courses = [...academyDB.courses, course];
	}

	return [200, course];
}); 

 mock.onPost('/api/academy-app/course/update').reply(request => {
	const data = JSON.parse(request.data);
	academyDB.courses = academyDB.courses.map(_course => {
		if (_course.id === data.id) {
			return _.merge(_course, data);
		}
		return _course;
	});

	return [200, data];
});
