import FuseAnimate from '@fuse/core/FuseAnimate';
import FuseChipSelect from '@fuse/core/FuseChipSelect';
import FuseLoading from '@fuse/core/FuseLoading';
import FusePageCarded from '@fuse/core/FusePageCarded';
import { useForm, useDeepCompareEffect } from '@fuse/hooks';
import FuseUtils from '@fuse/utils';
import _ from '@lodash';
//import Button from '@material-ui/core/Button';
import { orange } from '@material-ui/core/colors';
import Icon from '@material-ui/core/Icon';
//import InputAdornment from '@material-ui/core/InputAdornment';
import { makeStyles, useTheme } from '@material-ui/core/styles';
//import Tab from '@material-ui/core/Tab';
//import Tabs from '@material-ui/core/Tabs';
import TextField from '@material-ui/core/TextField';
import Typography from '@material-ui/core/Typography';
//import withReducer from 'app/store/withReducer';
import clsx from 'clsx';
import React, { useEffect, useState } from 'react';
//import { useDispatch, useSelector } from 'react-redux';
import { Link, useParams } from 'react-router-dom';
//import { saveProduct, newProduct, getProduct } from '../store/productSlice';
//import reducer from '../store';

const useStyles = makeStyles(theme => ({
    productImageFeaturedStar: {
        position: 'absolute',
        top: 0,
        right: 0,
        color: orange[400],
        opacity: 0
    },
    productImageUpload: {
        transitionProperty: 'box-shadow',
        transitionDuration: theme.transitions.duration.short,
        transitionTimingFunction: theme.transitions.easing.easeInOut
    },
    productImageItem: {
        transitionProperty: 'box-shadow',
        transitionDuration: theme.transitions.duration.short,
        transitionTimingFunction: theme.transitions.easing.easeInOut,
        '&:hover': {
            '& $productImageFeaturedStar': {
                opacity: 0.8
            }
        },
        '&.featured': {
            pointerEvents: 'none',
            boxShadow: theme.shadows[3],
            '& $productImageFeaturedStar': {
                opacity: 1
            },
            '&:hover $productImageFeaturedStar': {
                opacity: 1
            }
        }
    }
}));

function Product(props) {
  // dispatch  const dispatch = useDispatch();
   // const product = useSelector(({ eCommerceApp }) => eCommerceApp.product);
   // dispatchconst theme = useTheme();

    const classes = useStyles(props);
    //const [tabValue, setTabValue] = useState(0);
    const { form, handleChange, setForm } = useForm(null);
    const routeParams = useParams();

   /*  useDeepCompareEffect(() => {
        function updateProductState() {
            const { productId } = routeParams;

            if (productId === 'new') {
               // dispatch(newProduct());
            } else {
               // dispatch(getProduct(routeParams));
            }
        }

        updateProductState();
    }, [dispatch, routeParams]); */

   /*  useEffect(() => {
        if ((product && !form) || (product && form && product.id !== form.id)) {
            setForm(product);
        }
    }, [form, product, setForm]); */

    function handleChangeTab(event, value) {
      //  setTabValue(value);
    }

    function handleChipChange(value, name) {
        setForm(
            _.set(
                { ...form },
                name,
                value.map(item => item.value)
            )
        );
    }

    function setFeaturedImage(id) {
        setForm(_.set({ ...form }, 'featuredImageId', id));
    }

    function handleUploadChange(e) {
        const file = e.target.files[0];
        if (!file) {
            return;
        }
        const reader = new FileReader();
        reader.readAsBinaryString(file);

        reader.onload = () => {
            setForm(
                _.set({ ...form }, `images`, [
                    {
                        id: FuseUtils.generateGUID(),
                        url: `data:${file.type};base64,${btoa(reader.result)}`,
                        type: 'image'
                    },
                    ...form.images
                ])
            );
        };

        reader.onerror = () => {
            console.log('error on load image');
        };
    }

  /*   function canBeSubmitted() {
        return form.name.length > 0 && !_.isEqual(product, form);
    } */

   /*  if ((!product || (product && routeParams.productId !== product.id)) && routeParams.productId !== 'new') {
        return <FuseLoading />;
    } */

    return (
        <FusePageCarded
            classes={{
                toolbar: 'p-0',
                header: 'min-h-72 h-72 sm:h-136 sm:min-h-136'
            }}
           
         
            content={
                form && (
                    <div className="p-16 sm:p-24 max-w-2xl">
                        {tabValue === 0 && (
                            <div>
                                <TextField
                                    className="mt-8 mb-16"
                                    error={form.name === ''}
                                    required
                                    label="Name"
                                    autoFocus
                                    id="name"
                                    name="name"
                                    value={form.name}
                                    onChange={handleChange}
                                    variant="outlined"
                                    fullWidth
                                />

                                <TextField
                                    className="mt-8 mb-16"
                                    id="description"
                                    name="description"
                                    onChange={handleChange}
                                    label="Description"
                                    type="text"
                                    value={form.description}
                                    multiline
                                    rows={5}
                                    variant="outlined"
                                    fullWidth
                                />

                                <FuseChipSelect
                                    className="mt-8 mb-24"
                                    value={form.categories.map(item => ({
                                        value: item,
                                        label: item
                                    }))}
                                    onChange={value => handleChipChange(value, 'categories')}
                                    placeholder="Select multiple categories"
                                    textFieldProps={{
                                        label: 'Categories',
                                        InputLabelProps: {
                                            shrink: true
                                        },
                                        variant: 'outlined'
                                    }}
                                    isMulti
                                />

                                <FuseChipSelect
                                    className="mt-8 mb-16"
                                    value={form.tags.map(item => ({
                                        value: item,
                                        label: item
                                    }))}
                                    onChange={value => handleChipChange(value, 'tags')}
                                    placeholder="Select multiple tags"
                                    textFieldProps={{
                                        label: 'Tags',
                                        InputLabelProps: {
                                            shrink: true
                                        },
                                        variant: 'outlined'
                                    }}
                                    isMulti
                                />
                            </div>
                        )}
                        {tabValue === 1 && (
                            <div>
                                <div className="flex justify-center sm:justify-start flex-wrap -mx-8">
                                    <label
                                        htmlFor="button-file"
                                        className={clsx(
                                            classes.productImageUpload,
                                            'flex items-center justify-center relative w-128 h-128 rounded-8 mx-8 mb-16 overflow-hidden cursor-pointer shadow-1 hover:shadow-5'
                                        )}
                                    >
                                        <input
                                            accept="image/*"
                                            className="hidden"
                                            id="button-file"
                                            type="file"
                                            onChange={handleUploadChange}
                                        />
                                        <Icon fontSize="large" color="action">
                                            cloud_upload
										</Icon>
                                    </label>
                                    {form.images.map(media => (
                                        <div
                                            onClick={() => setFeaturedImage(media.id)}
                                            onKeyDown={() => setFeaturedImage(media.id)}
                                            role="button"
                                            tabIndex={0}
                                            className={clsx(
                                                classes.productImageItem,
                                                'flex items-center justify-center relative w-128 h-128 rounded-8 mx-8 mb-16 overflow-hidden cursor-pointer shadow-1 hover:shadow-5',
                                                media.id === form.featuredImageId && 'featured'
                                            )}
                                            key={media.id}
                                        >
                                            <Icon className={classes.productImageFeaturedStar}>star</Icon>
                                            <img className="max-w-none w-auto h-full" src={media.url} alt="product" />
                                        </div>
                                    ))}
                                </div>
                            </div>
                        )}
                    </div>
                )
            }
        />
    );
}

export default (Product);
