import React from 'react';
import FuseExample from '@fuse/core/FuseExample';
import FuseHighlight from '@fuse/core/FuseHighlight';
import Button from '@material-ui/core/Button';
import Icon from '@material-ui/core/Icon';
import Typography from '@material-ui/core/Typography';
import {makeStyles} from '@material-ui/core/styles';
/* eslint import/no-webpack-loader-syntax: off */
/* eslint no-unused-vars: off */
/* eslint-disable jsx-a11y/accessible-emoji */
const useStyles = makeStyles(theme => ({
    layoutRoot: {
        '& .description': {
            marginBottom: 16
        }
    }
}));

function MenusDoc(props)
{
    const classes = useStyles();
    return (

        <>
            <div className="flex flex-1 flex-grow-0 items-center justify-end">
                <Button
                    className="normal-case"
                    variant="outlined"
                    component="a"
                    href="https://material-ui.com/components/menus"
                    target="_blank"
                    role="button"
                >
                    <Icon>link</Icon>
                    <span className="mx-4">Reference</span>
                </Button>
            </div>
            <Typography className="text-44 mt-32 mb-8" component="h1">Menus</Typography>
            <Typography className="description">Menus display a list of choices on temporary surfaces.</Typography>

            <Typography className="mb-16" component="div">A <a href="https://material.io/design/components/menus.html">Menu</a> displays a list of choices on a temporary surface. It appears when the
                user interacts with a button, or other control.</Typography>
            <Typography className="text-32 mt-32 mb-8" component="h2">Simple Menu</Typography>
            <Typography className="mb-16" component="div">Simple menus open over the anchor element by default (this option can be changed via props). When close to a screen edge, simple menus
                vertically realign to make sure that all menu items are completely visible.</Typography>
            <Typography className="mb-16" component="div">Choosing an option should immediately ideally commit the option and close the menu.</Typography>
            <Typography className="mb-16" component="div"><strong>Disambiguation</strong>: In contrast to simple menus, simple dialogs can present additional detail related to the options available
                for a list item or provide navigational or orthogonal actions related to the primary task. Although they can display the same content, simple menus are preferred over simple dialogs
                because simple menus are less disruptive to the user’s current context.</Typography>
            <Typography className="mb-16" component="div"><FuseExample
                className="my-24"
                iframe={false}
                component={require('app/main/documentation/material-ui-components/components/menus/SimpleMenu.js').default}
                raw={require('!raw-loader!app/main/documentation/material-ui-components/components/menus/SimpleMenu.js')}
