import React from 'react';
import { makeStyles } from '@material-ui/core/styles';
import Stepper from '@material-ui/core/Stepper';
import Step from '@material-ui/core/Step';
import StepLabel from '@material-ui/core/StepLabel';
import Button from '@material-ui/core/Button';
import Typography from '@material-ui/core/Typography';
import BasicTextFields from './GoesEmpresas';



const useStyles = makeStyles((theme) => ({
    root: {
        width: '100%',
    },
    backButton: {
        marginRight: theme.spacing(1),
    },
    instructions: {
        marginTop: theme.spacing(1),
        marginBottom: theme.spacing(1),
    },
}));


function getSteps() {
    return [
        'Crear Grupos Estudiantes GAES',
        '¿ Que Tipo de Empesa quieres ?',
        '¿ Como se llamará la Empresa ?',
        '¿ Cuales son los dato de contacto de la Empresa ?',
        '¿ Es necesario definir quien es Empresa y hacia donde va ?',
        '¿ Ahora vamos a crear el modelo de negocio de la Empresa ?',
        '¿ solo falta el logotipo de la Empresa ?',
    ];
}


function getStepContent(stepIndex) {
    switch (stepIndex) {
        case 0:
            return <BasicTextFields/>
        case 1:
            return 'What is an ad group anyways?';
        case 2:
            return 'This is the bit I really care about!';
        case 3:
            return 'What is an ad group anyways?';
        case 4:
            return 'This is the bit I really care about!';
        case 5:
            return 'This is the bit I really care about!';
        case 6:
            return 'What is an ad group anyways?';
        default:
            return 'Unknown stepIndex';
    }
}

export default function HorizontalLabelPositionBelowStepper() {
    const classes = useStyles();
    const [activeStep, setActiveStep] = React.useState(0);
    const steps = getSteps();

    const handleNext = () => {
        setActiveStep((prevActiveStep) => prevActiveStep + 1);
    };

    const handleBack = () => {
        setActiveStep((prevActiveStep) => prevActiveStep - 1);
    };

    const handleReset = () => {
        setActiveStep(0);
    };

    return (
        <div className={classes.root}>
            <Stepper activeStep={activeStep} alternativeLabel>
                {steps.map((label) => (
                    <Step key={label}>
                        <StepLabel>{label}</StepLabel>
                    </Step>
                ))}
            </Stepper>
            <div>
                {activeStep === steps.length ? (
                    <div>
                        <Typography className={classes.instructions}>
                            All steps completed
                            </Typography>
                        <Button onClick={handleBack}>Reset</Button>
                      {/*   <Button onClick={handleReset}>Reset</Button> */}
                    </div>
                ) : (
                        <div>
                            <Typography className={classes.instructions}>{getStepContent(activeStep)}</Typography>
                            <div>
                                <Button
                                    disabled={activeStep === 0}
                                    onClick={handleBack}
                                    className={classes.backButton}
                                >
                                    Atras
                               </Button>
                                <Button variant="contained" color="primary" onClick={handleNext}>
                                    {activeStep === steps.length - 1 ? 'Fin' : 'Siguiente'}
                                </Button>
                            </div>
                        </div>
                    )}
            </div>
        </div>
    );
}
