import React from 'react';
import FuseExample from '@fuse/core/FuseExample';
import FuseHighlight from '@fuse/core/FuseHighlight';
import Button from '@material-ui/core/Button';
import Icon from '@material-ui/core/Icon';
import Typography from '@material-ui/core/Typography';
import {makeStyles} from '@material-ui/core/styles';
/* eslint import/no-webpack-loader-syntax: off */
/* eslint no-unused-vars: off */
/* eslint-disable jsx-a11y/accessible-emoji */
const useStyles = makeStyles(theme => ({
    layoutRoot: {
        '& .description': {
            marginBottom: 16
        }
    }
}));

function AlertDoc(props)
{
    const classes = useStyles();
    return (

        <>
            <div className="flex flex-1 flex-grow-0 items-center justify-end">
                <Button
                    className="normal-case"
                    variant="outlined"
                    component="a"
                    href="https://material-ui.com/components/alert"
                    target="_blank"
                    role="button"
                >
                    <Icon>link</Icon>
                    <span className="mx-4">Reference</span>
                </Button>
            </div>
            <Typography className="text-44 mt-32 mb-8" component="h1">Alert</Typography>
            <Typography className="description">An alert displays a short, important message in a way that attracts the user's attention without interrupting the user's task.</Typography>

            <Typography className="mb-16" component="div"><strong>Note:</strong> This component is not documented in the <a href="https://material.io/">Material Design guidelines</a>, but Material-UI
                supports it.</Typography>
            <Typography className="text-32 mt-32 mb-8" component="h2">Simple alerts</Typography>
            <Typography className="mb-16" component="div">The alert offers four severity levels that set a distinctive icon and color.</Typography>
            <Typography className="mb-16" component="div"><FuseExample
                className="my-24"
                iframe={false}
                component={require('app/main/documentation/material-ui-components/components/alert/SimpleAlerts.js').default}
                raw={require('!raw-loader!app/main/documentation/material-ui-components/components/alert/SimpleAlerts.js')}
            /></Typography>
            <Typography className="text-32 mt-32 mb-8" component="h2">Description</Typography>
            <Typography className="mb-16" component="div">You can use the <code>{`AlertTitle`}</code> component to display a formatted title above the content.</Typography>
            <Typography className="mb-16" component="div"><FuseExample
                className="my-24"
                iframe={false}
                component={require('app/main/documentation/material-ui-components/components/alert/DescriptionAlerts.js').default}
                raw={require('!raw-loader!app/main/documentation/material-ui-components/components/alert/DescriptionAlerts.js')}
            /></Typography>
            <Typography className="text-32 mt-32 mb-8" component="h2">Actions</Typography>
            <Typography className="mb-16" component="div">An alert can have an action, such as a close or undo button.
                It is rendered after the message, at the end of the alert.</Typography>
            <Typography className="mb-16" component="div">If an <code>{`onClose`}</code> callback is provided and no <code>{`action`}</code> prop is set, a close icon is displayed.
                The <code>{`action`}</code> prop can be used to provide an alternative action, for example using a Button or IconButton.</Typography>
            <Typography className="mb-16" component="div"><FuseExample
                className="my-24"
                iframe={false}
                component={require('app/main/documentation/material-ui-components/components/alert/ActionAlerts.js').default}
                raw={require('!raw-loader!app/main/documentation/material-ui-components/components/alert/ActionAlerts.js')}
            /></Typography>
            <Typography className="text-24 mt-32 mb-8" component="h3">Transition</Typography>
            <Typography className="mb-16" component="div">You can use a <a href="/components/transitions/">transition component</a> such as <code>{`Collapse`}</code> to transition the appearance of
                the alert.</Typography>
            <Typography className="mb-16" component="div"><FuseExample
                className="my-24"
                iframe={false}
                component={require('app/main/documentation/material-ui-components/components/alert/TransitionAlerts.js').default}
                raw={require('!raw-loader!app/main/documentation/material-ui-components/components/alert/TransitionAlerts.js')}
            /></Typography>
            <Typography className="text-32 mt-32 mb-8" component="h2">Icons</Typography>
            <Typography className="mb-16" component="div">The <code>{`icon`}</code> prop allows you to add an icon to the beginning of the alert component.
                This will override the default icon for the specified severity.</Typography>
            <Typography className="mb-16" component="div">You can change the default severity to icon mapping with the <code>{`iconMapping`}</code> prop. This can be defined globally using <a
                href="/customization/globals/#default-props">theme customization</a>.</Typography>
            <Typography className="mb-16" component="div">Setting the icon prop to false will remove the icon altogether.</Typography>
            <Typography className="mb-16" component="div"><FuseExample
                className="my-24"
                iframe={false}
                component={require('app/main/documentation/material-ui-components/components/alert/IconAlerts.js').default}
                raw={require('!raw-loader!app/main/documentation/material-ui-components/components/alert/IconAlerts.js')}
            /></Typography>
            <Typography className="text-32 mt-32 mb-8" component="h2">Variants</Typography>
