import React from 'react';
import FuseExample from '@fuse/core/FuseExample';
import FuseHighlight from '@fuse/core/FuseHighlight';
import Button from '@material-ui/core/Button';
import Icon from '@material-ui/core/Icon';
import Typography from '@material-ui/core/Typography';
import {makeStyles} from '@material-ui/core/styles';
/* eslint import/no-webpack-loader-syntax: off */
/* eslint no-unused-vars: off */
/* eslint-disable jsx-a11y/accessible-emoji */
const useStyles = makeStyles(theme => ({
    layoutRoot: {
        '& .description': {
            marginBottom: 16
        }
    }
}));

function CssBaselineDoc(props)
{
    const classes = useStyles();
    return (

        <>
            <div className="flex flex-1 flex-grow-0 items-center justify-end">
                <Button
                    className="normal-case"
                    variant="outlined"
                    component="a"
                    href="https://material-ui.com/components/css-baseline"
                    target="_blank"
                    role="button"
                >
                    <Icon>link</Icon>
                    <span className="mx-4">Reference</span>
                </Button>
            </div>
            <Typography className="text-44 mt-32 mb-8" component="h1">CSS Baseline</Typography>
            <Typography className="description">Material-UI provides a CssBaseline component to kickstart an elegant, consistent, and simple baseline to build upon.</Typography>

            <Typography className="text-32 mt-32 mb-8" component="h2">Global reset</Typography>
            <Typography className="mb-16" component="div">You might be familiar with <a href="https://github.com/necolas/normalize.css">normalize.css</a>, a collection of HTML element and attribute
                style-normalizations.</Typography>

            <FuseHighlight component="pre" className="language-jsx">
                {` 
import React from 'react';
import CssBaseline from '@material-ui/core/CssBaseline';

export default function MyApp() {
  return (
    <React.Fragment>
      <CssBaseline />
      {/* The rest of your application */}
    </React.Fragment>
  );
}
`}
            </FuseHighlight>
            <Typography className="text-32 mt-32 mb-8" component="h2">Scoping on children</Typography>
            <Typography className="mb-16" component="div">However, you might be progressively migrating a website to Material-UI, using a global reset might not be an option.
                It&#39;s possible to apply the baseline only to the children by using the <code>{`ScopedCssBaseline`}</code> component.</Typography>

            <FuseHighlight component="pre" className="language-jsx">
                {` 
import React from 'react';
import ScopedCssBaseline from '@material-ui/core/ScopedCssBaseline';
import MyApp from './MyApp';

export default function MyApp() {
  return (
    <ScopedCssBaseline>
      {/* The rest of your application */}
      <MyApp />
    </ScopedCssBaseline>
  );
}
`}
            </FuseHighlight>
            <Typography className="mb-16" component="div">⚠️ Make sure you import <code>{`ScopedCssBaseline`}</code> first to avoid box-sizing conflicts as in the above example.</Typography>
            <Typography className="text-32 mt-32 mb-8" component="h2">Approach</Typography>
            <Typography className="text-24 mt-32 mb-8" component="h3">Page</Typography>
            <Typography className="mb-16" component="div">The <code>{`<html>`}</code> and <code>{`<body>`}</code> elements are updated to provide better page-wide defaults. More
                specifically:</Typography>
            <ul>
                <li>The margin in all browsers is removed.</li>
                <li>The default Material Design background color is applied.
                    It&#39;s using <a href="/customization/default-theme/?expand-path=$.palette.background"><code>{`theme.palette.background.default`}</code></a> for standard devices and a white
                    background for print devices.
                </li>
            </ul>
            <Typography className="text-24 mt-32 mb-8" component="h3">Layout</Typography>
            <ul>
                <li><code>{`box-sizing`}</code> is set globally on the <code>{`<html>`}</code> element to <code>{`border-box`}</code>.
                    Every element—including <code>{`*::before`}</code> and <code>{`*::after`}</code> are declared to inherit this property,
                    which ensures that the declared width of the element is never exceeded due to padding or border.
                </li>
            </ul>
            <Typography className="text-24 mt-32 mb-8" component="h3">Typography</Typography>
            <ul>
                <li>No base font-size is declared on the <code>{`<html>`}</code>, but 16px is assumed (the browser default).
                    You can learn more about the implications of changing the <code>{`<html>`}</code> default font size in <a href="/customization/typography/#typography-html-font-size">the theme
                        documentation</a> page.
                </li>
                <li>Set the <code>{`theme.typography.body2`}</code> style on the <code>{`<body>`}</code> element.</li>
                <li>Set the font-weight to <code>{`theme.typography.fontWeightBold`}</code> for the <code>{`<b>`}</code> and <code>{`<strong>`}</code> elements.</li>
                <li>Custom font-smoothing is enabled for better display of the Roboto font.</li>
            </ul>
            <Typography className="text-32 mt-32 mb-8" component="h2">Customization</Typography>
            <Typography className="mb-16" component="div">Head to the <a href="/customization/globals/#global-css">global customization</a> section of the documentation to change the output of these
                components.</Typography>

        </>

    );
}

export default CssBaselineDoc;
